const specialTypes = [
  "IT.FO.04-Hỗ trợ khác",
  "Yêu cầu cài đặt phần mềm mới - Nâng cấp phần mềm",
  "Yêu cầu cấp tài sản công nghệ thông tin",
  "Yêu cầu cấp phụ kiện tài sản công nghệ thông tin",
  "Kiểm tra thông tin Hardware",
  "Hỗ trợ xử lý lỗi phần cứng",
  "Hỗ trợ xử lý lỗi phần mềm",
  "Yêu cầu liên quan đến cổng USB",
  "Khắc phục sự cố mạng"
];

// input 0 = list_execute_trungnd
const listData = $('list_execute_trungnd').first().json.ListData || [];

// input 1 = checkin_trungnd
const checkinRaw = $input.first().json.data || "{}";

/** @type {{ [key: string]: any }} */
let checkinData = {};
try {
  checkinData = JSON.parse(checkinRaw).checkin || {};
} catch (e) {
  checkinData = {};
}

// Lấy ngày hiện tại dạng dd-mm-yyyy
const now = new Date();
const day = String(now.getDate()).padStart(2, '0');
const month = String(now.getMonth() + 1).padStart(2, '0');
const year = now.getFullYear();
const todayStr = `${day}-${month}-${year}`;

// kiểm tra onLeaveToday
const onLeaveToday = !checkinData[todayStr];

// Kiểm tra có ticket đặc biệt
let hasSpecialTicket = false;
for (const ticket of listData) {
  if (specialTypes.includes(ticket.requestType)) {
    hasSpecialTicket = true;
    break;
  }
}

const isAvailable = !onLeaveToday && !hasSpecialTicket;

return [{
  json: {
    user: "trungnd",
    isAvailable
  }
}];

