={{ (() => {
  const username = $('select_IT_HCM').item.json.name?.trim();
  const map = {
    'huynd54': 'Nguyễn Đắc Huy',
    'trongnc2': 'Nguyễn Công Trọng',
    'sanglt6': 'Lê Thanh Sang',
    'huuduc': 'Nguyễn Hữu Đức',
    'trungnd': 'Nguyễn Đức Trung',
    'anhvt66': 'Vũ Trường Anh'
  };

  // Trường hợp đặc biệt cho binhvh
  if (username === 'binhvh') {
    return 'Do hiện tại tất cả các IT đang bận hỗ trợ các công việc khác nên yêu cầu của bạn sẽ được trưởng bộ phận IT điều phối lại ngay sau khi có IT sẵn sàng';
  }

  const result = map[username] || username;
  return (result + '').replace(/[\n\r\u2028\u200B]+/g, '').trim();
})() }}

